mapper.select:
SELECT d.ID, d.DispatchBillID, d.ShoppingAddressID, d.DeliveryUser, d.STATUS, d.UnitPrice, d.PurchaseQuantity,d.Amount, d.DeliveryTime, getAddress(d.ShoppingAddressID) as ShoppingAddressName, getUserName(d.DeliveryUser) as DeliveryUserName, cp.CompanyID, getProductName(cp.ProductID) as ProductName FROM DispatchBill d LEFT JOIN CompanyProducts cp ON d.CompanyProductID=cp.ID WHERE cp.CompanyID=#{companyId}
AND getAddress(d.ShoppingAddressID) like concat('%',#{page.shoppingAddressName},'%')
AND d.DispatchBillID=#{page.dispatchBillId}
AND getUserName(d.DeliveryUser) like concat('%',#{page.deliveryUserName},'%')
AND d.STATUS=#{page.status}
order by d.ID DESC limit #{page.begin},#{page.pageNum};

select count(d.ID) FROM DispatchBill d LEFT JOIN CompanyProducts cp ON d.CompanyProductID=cp.ID WHERE cp.CompanyID=#{companyId}
AND getAddress(d.ShoppingAddressID) like concat('%',#{page.shoppingAddressName},'%')
AND d.DispatchBillID=#{page.dispatchBillId}
AND getUserName(d.DeliveryUser) like concat('%',#{page.deliveryUserName},'%')
AND d.STATUS=#{page.status}
order by d.ID

SELECT d.ID, d.DispatchBillID, d.OrderID,o.OrderID as OrderCode, d.CompanyProductID, d.ProductUnit, d.UnitPrice, d.Amount, d.StorageID, d.ShoppingAddressID, d.DeliveryUser, d.DeliveryTime,d.Accepter, d.AcceptTime, d.CheckUser, d.CheckTime, d.STATUS, d.PurchaseQuantity,p.ProductModel, d.PackagesNumber,getAddress(d.ShoppingAddressID) as ShoppingAddressName, getUserName(d.DeliveryUser) as DeliveryUserName, getUserName(d.Accepter) as AccepterUserName, getUserName(d.CheckUser) as CheckUserName, getProductName(cp.ProductID) as ProductName, getStorageName(d.StorageID) as StorageName, getProductPic(cp.ProductID) as ProductPic, getCompanyName(cp.CompanyID) as SupplierName FROM DispatchBill d LEFT JOIN Orders o ON o.id =d.orderId LEFT JOIN CompanyProducts cp ON d.CompanyProductID=cp.ID LEFT JOIN Products p ON p.ID=cp.ProductID
LEFT JOIN Companys c ON c.ID=cp.CompanyID where c.ID =#{companyId} and d.Status=0
LEFT JOIN Users u ON d.DeliveryUser = u.id where u.ID = #{userId} and d.Status=2
LEFT JOIN Users u ON d.CheckUser = u.id where u.ID = #{userId} and d.Status=3
LEFT JOIN Users u ON o.userId = u.id where d.Status=4
and u.CompanyID = #{companyId}
LEFT JOIN Users u ON d.Accepter = u.id where u.ID = #{userId} and d.Status=5
LEFT JOIN Users u ON o.userId = u.id WHERE 1=1
and u.ID = #{userId}
AND d.OrderID=#{id}
and o.OrderID like CONCAT('%',#{orderCode},'%')
and getProductName(cp.ProductID) like CONCAT('%',#{productName},'%')
and getCompanyName(cp.CompanyID) like CONCAT('%',#{supplierName},'%')
AND d.STATUS=#{status}
LEFT JOIN Users u ON o.userId = u.id WHERE 1=1
and u.CompanyID = #{companyId}
and o.OrderID like CONCAT('%',#{orderCode},'%')
and getProductName(cp.ProductID) like CONCAT('%',#{productName},'%')
and getCompanyName(cp.CompanyID) like CONCAT('%',#{supplierName},'%')
AND d.STATUS=#{status}
LEFT JOIN Users u ON o.userId = u.id WHERE 1=1
and u.DepartmentID = #{departmentId}
and o.OrderID like CONCAT('%',#{orderCode},'%')
and getProductName(cp.ProductID) like CONCAT('%',#{productName},'%')
and getCompanyName(cp.CompanyID) like CONCAT('%',#{supplierName},'%')
AND d.STATUS=#{status}
order by d.Id Desc limit #{page.begin},#{page.pageNum}

SELECT COUNT(d.id) FROM DispatchBill d LEFT JOIN Orders o ON o.id =d.orderId LEFT JOIN CompanyProducts cp ON d.CompanyProductID=cp.ID
LEFT JOIN Companys c ON c.ID=cp.CompanyID where c.ID =#{companyId} and d.Status=0
LEFT JOIN Users u ON d.DeliveryUser = u.id where u.ID = #{userId} and d.Status=2
LEFT JOIN Users u ON d.CheckUser = u.id where u.ID = #{userId} and d.Status=3
LEFT JOIN Users u ON o.userId = u.id where d.Status=4
and u.CompanyID = #{companyId}
LEFT JOIN Users u ON d.Accepter = u.id where u.ID = #{userId} and d.Status=5
LEFT JOIN Users u ON o.userId = u.id WHERE 1=1
and u.ID = #{userId}
AND d.OrderID=#{id}
and o.OrderID like CONCAT('%',#{orderCode},'%')
and getProductName(cp.ProductID) like CONCAT('%',#{productName},'%')
and getCompanyName(cp.CompanyID) like CONCAT('%',#{supplierName},'%')
AND d.STATUS=#{status}
LEFT JOIN Users u ON o.userId = u.id WHERE 1=1
and u.CompanyID = #{companyId}
and o.OrderID like CONCAT('%',#{orderCode},'%')
and getProductName(cp.ProductID) like CONCAT('%',#{productName},'%')
and getCompanyName(cp.CompanyID) like CONCAT('%',#{supplierName},'%')
AND d.STATUS=#{status}
LEFT JOIN Users u ON o.userId = u.id WHERE 1=1
and u.DepartmentID = #{departmentId}
and o.OrderID like CONCAT('%',#{orderCode},'%')
and getProductName(cp.ProductID) like CONCAT('%',#{productName},'%')
and getCompanyName(cp.CompanyID) like CONCAT('%',#{supplierName},'%')
AND d.STATUS=#{status}

mapper.update:
UPDATE DispatchBill SET Status=#{status}

	,Accepter=#{userId}


	,DeliveryUser=#{userId},DeliveryTime=now()

WHERE FIND_IN_SET(ID,#{ids})>0
