mapper.insert:
INSERT INTO Jobs (
DepartmentID, JobName, Status, CreateBy, CreateAt
) VALUES (
 #{departmentId}, #{jobName}, #{status}, #{createBy}, #{createAt}
)

mapper.update:
UPDATE Jobs
SET
ID=#{id}, DepartmentID=#{departmentId}, JobName=#{jobName},
Status=#{status}, CreateBy=#{createBy}, CreateAt=#{createAt}
WHERE
ID=#{id}

UPDATE Jobs
SET
JobName=#{jobName}
WHERE
ID=#{id}
