mapper.select:
select id,userId,inquiryId,currency,deliveryType,productModel,productId, productQuantity,productUnit,refPrice,expectPrice,expectTime,orderTime,status, getProductName(ProductID) AS productName, getProductPic(ProductID) AS productPic from InquirySheet where userId=#{userId} and FIND_IN_SET(status,'0,1')
and inquiryId = #{page.inquiryId}
and FIND_IN_SET(productId,getProductIdByProductName(#{page.productName}))
and orderTime >= #{page.inquiryTimeStart}
and orderTime <= #{page.inquiryTimeStop}
order by ID DESC limit #{page.begin},#{page.pageNum};

select count(ID) from InquirySheet where userId=#{userId}
and
FIND_IN_SET(status,'0,1')

	and inquiryId = #{page.inquiryId}


	and
	FIND_IN_SET(productId,getProductIdByProductName(#{page.productName}))


	and orderTime >= #{page.inquiryTimeStart}


	and orderTime <= #{page.inquiryTimeStop}

select
count(id) from InquirySheet
where userId=#{userId}

mapper.update:
update
InquirySheet set status = 1
where InquiryID=#{inquiryId}

update InquirySheet
set Status = #{status}
where FIND_IN_SET(inquiryId,#{ids})>0
