mapper.insert:
insert into Roles (RoleName,
RoleCode, Status, CreateTime, CreateBy,CompanyID
) values(
#{roleName},
#{roleCode},
#{status},
NOW(),
#{createBy},
#{companyID}
)

mapper.select:
select ID, RoleName, RoleCode, Status, CreateTime,
CreateBy,RightID
from Roles where id=#{id}

mapper.update:
update Roles set
status=#{status} where
id=#{id}
