mapper.select:
select ID, SettlementID, Currency, SettleAmount, SettleFee,
SettleBy, SettleTime, Status, BankAccountID, SettleType, IsInvoice
from Settlement where id=#{id}

SELECT s.id,s.settlementId,s.currency,s.settleAmount,s.settleFee,u.userName,
s.settleTime,s.`status`,s.companyId,s.settleType,s.bankAccount 
from Settlement s
LEFT JOIN Users u ON u.id = s.settleBy 
where s.companyId = #{companyId}

AND s.SettleTime BETWEEN #{startTime} AND #{endTime}

ORDER BY s.id DESC
limit #{page.begin},#{page.pageNum}

SELECT COUNT(asd.id) from (SELECT s.id,s.SettlementID,s.Currency,s.SettleAmount,s.SettleFee,u.UserName,
s.SettleTime,s.`Status`,s.CompanyId,s.SettleType,s.BankAccount 
from Settlement s
LEFT JOIN Users u ON u.ID = s.SettleBy
where s.companyId =#{companyId}
AND s.SettleTime BETWEEN #{startTime} AND #{endTime}
)asd

SELECT s.id,s.settlementId,s.settleAmount,u.userName,
s.settleTime,s.status,s.companyId
from Settlement s
LEFT JOIN Users u ON u.id = s.settleBy 
where s.companyId = #{companyId}

and s.settlementId=#{page.settlementId}


and s.settleTime >= #{page.settleTimeStart}


and s.settleTime <= #{page.settleTimeStop}

order by s.id DESC limit #{page.begin},#{page.pageNum};

SELECT count(s.id) 
from Settlement s
LEFT JOIN Users u ON u.id = s.settleBy 
where s.companyId = #{companyId}

and s.settlementId=#{page.settlementId}


and s.settleTime >= #{page.settleTimeStart}


and s.settleTime <= #{page.settleTimeStop}
